import React, { Component } from 'react';
import BoardItem from './BoardItem';
import './App.css';


class InvoiceProcess extends Component {
    constructor(props) {
        super(props);
        this.state = {
            dataList:[]
            , dataDetail:{}
            , boards:[]
            , selectedBoard:{}
            , imgDir:undefined
            , id:undefined
            , IP:'127.0.0.1:8728'
            , rootDir : 'D:\\WorkSpace\\workspace_react\\public\\'
        }
    }

    callGetListData = (e) => {
        const getDate = window.document.getElementById("getDate").value;
        fetch("http://"+this.state.IP+"/RPA/AirWayBill/getDatabase", {
            method: 'POST',
            headers: {
                Accept: 'application/json'
                , 'Content-Type': 'application/json'
            },
            body: JSON.stringify({
                methodName: 'getListData'
                , date: getDate
            })
        })
        .then(res => res.json())
        .then(json => {
            this.setState({
                dataList:JSON.parse(json)
            })
        })
        .catch((error) => {
            console.error(error);
        });
    }
    callGetOneData = (idParam, imagePath) => {
        fetch("http://"+this.state.IP+"/RPA/AirWayBill/getDatabase", {
            method: 'POST',
            headers: {
                Accept: 'application/json'
                , 'Content-Type': 'application/json'
            },
            body: JSON.stringify({
                methodName: 'getOneData'
                , id: idParam
            })
        })
        .then(res => res.json())
        .then(json => {
            this.setState({
                dataDetail:JSON.parse(json)
                , id: idParam
            })
        })
        .catch((error) => {
            console.error(error);
        });
        this.callGetImage(idParam, imagePath)
    }
    callGetImage = (idParam, imagePath) => {
        const filePath = this.state.rootDir + imagePath;
        let formData = new FormData();
        formData.append('methodName', 'getImage');
        formData.append('imagePath', imagePath);
        // formData.append('uploadFile', document.getElementById("imagePng").files[0]);

        fetch("http://"+this.state.IP+"/RPA/AirWayBill/getOCR", {
            method: 'POST',
            header:{
                "Content-Disposition: attachment;filename":imagePath
            },
            body: formData
        })
        .then(res => res.blob())
        .then(blob => {
            var url = window.URL.createObjectURL(blob);
            var a = document.createElement('a');
            a.href = url;
            a.download = filePath;
            a.filename = filePath;
            document.body.appendChild(a); // we need to append the element to the dom -> otherwise it will not work in firefox
            a.click();    
            a.remove();  //afterwards we remove the element again  
        })
        .catch((error) => {
            console.error(error);
        });
    }
    fileUpload = (idParam) => {
        // if ( document.getElementById("uploadText").value == "" ) {
        //     alert('uploadText should not be an empty field.');
        //     return ;
        // }

        let formData = new FormData();
        formData.append('methodName', 'FileUpload');
        // formData.append('text', document.getElementById("uploadText").value);
        // formData.append('uploadFile', document.getElementById("imagePng").files[0]);

        fetch("http://"+this.state.IP+"/RPA/Invoice/getOCR", {
            method: 'POST',
            body: formData
        })
        .then(res => res.json())
        .then(json => {
            this.setState({
                id: idParam
            })
        })
        .catch((error) => {
            console.error(error);
        });
    }

    createTextBox = () =>{
        const valueArray = this.state.dataDetail;

        if (valueArray && valueArray.value &&  valueArray.value.length > 0){
            document.getElementById("txtPanel").innerHTML = "";

            let txt
            for (const [key, value] of Object.entries(valueArray.value)) {
                console.log(key, value);
                // const size = value.edCoordX-value.stCoordX;
                console.log(value.stCoordX);
                // txt = "<div style='margin: " + value.stCoordX + "px " +value.stCoordY+"px'><input type='text' size=" + value.text.length + " value='" + value.text + "' /><br/></div>";
                txt = "<input type='text' size=" + value.text.length + " value='" + value.text + "' /><br/>";
                console.log(txt);
                document.getElementById("txtPanel").innerHTML += txt;
            }
        }else if(document.getElementById("txtPanel")){
            document.getElementById("txtPanel").innerHTML = "";
        }
    };


    render() {
        this.createTextBox();
        let dataValue = [];
        let imgDirectory = ""
        if (this.state.dataList.value !== undefined){
            dataValue = this.state.dataList.value;
        }
        if (this.state.imgDir !== undefined){
            imgDirectory = this.state.imgDir;
        }
        const divStyle = { 
            backgroundImage: 'url(' + imgDirectory + ')' 
            ,backgroundPosition: 'cover'
            ,backgroundRepeat: 'noRepeat'
            ,backgroundSize: 'cover'
            ,width: '850px'
            // ,height: '1200px'
            , height: 'auto'
        };
        return (
            <div>
                <h1>Invoice Data Process</h1><p/>
                Upload file(zip, pdf, png) : <input type="file" id="imagePng" name="imagePng" accept="image/png, image/jpeg"></input>
                {/* <input type="text" id="uploadText"></input> */}
                <button type="button" onClick={this.fileUpload}>File Upload</button>
                <p/>
                Date : <input type="text" id="getDate"></input>
                <button type="button" onClick={this.callGetListData}>go Query</button>
                <p/>
                <table border="1">
                    <tbody>
                        <tr align="center">
                            <td width="50">id</td>
                            <td width="500">text</td>
                            <td width="500">imageDir</td>
                            <td width="100">createDay</td>
                            <td width="100">createTime</td> 
                            <td width="100">updateDay</td>
                            <td width="100">updateTime</td>
                        </tr>
                        {
                            dataValue.map(row =>
                                (<BoardItem key={row.id} row={row} clickEvent={function(path, idParam){
                                    this.setState({imgDir:path, id:idParam});
                                    this.callGetOneData(idParam, path);
                                }.bind(this)}
                                />)
                            )   
                        }
                    </tbody>
                </table>
                <table border="1" width="1600" height="720">
                    <tbody>
                        <td align="center">
                            <img src={imgDirectory} style={divStyle} ></img>
                        </td>
                        <td align="center">
                            {/* <img src={imgDirectory} class='imgClass'></img> */}
                            <div id="txtPanel" align="left" style={divStyle} ></div>
                        </td> 
                    </tbody>
                </table>
            </div>
        );
    }
}

export default InvoiceProcess;
