import React, { Component } from 'react';
import BoardItem from './BoardItem';
import './App.css';


class InvoiceProcess extends Component {
    constructor(props) {
        super(props);
        this.state = {
            dataList:[]
            // , dataDetail:{}
            // , boards:[]
            // , selectedBoard:{}
            // , imgDir:undefined
            // , id:undefined
            , IP:'127.0.0.1:8728'
            , rootDir : 'D:\\WorkSpace\\workspace_RPA\\invoice\\public\\'
        }
    }

    callGetListData = (e) => {
        const getDate = window.document.getElementById("getDate").value;
        fetch("http://"+this.state.IP+"/RPA/Invoice/getOCR", {
            method: 'POST',
            headers: {
                Accept: 'application/json'
                , 'Content-Type': 'application/json'
            },
            body: JSON.stringify({
                methodName: 'ListData'
                , date: getDate
            })
        })
        .then(res => res.json())
        .then(json => {
            this.setState({
                dataList:JSON.parse(json)
            })
        })
        .catch((error) => {
            console.error(error);
        });
    }


    fileUpload = (idParam) => {
        let formData = new FormData();
        formData.append('methodName', 'FileUpload');

        for (const file of document.getElementById("uploadFIle").files) {
            formData.append('uploadFile',file,file.name)
        }

        fetch("http://"+this.state.IP+"/RPA/Invoice/getOCR", {
            method: 'POST',
            body: formData
        })
        .then(res => res.json())
        .then(json => {
            this.setState({
                id: idParam
            })
        })
        .catch((error) => {
            console.error(error);
        });
    }

    // createTextBox = () =>{
    //     const valueArray = this.state.dataDetail;

    //     if (valueArray && valueArray.value &&  valueArray.value.length > 0){
    //         document.getElementById("txtPanel").innerHTML = "";

    //         let txt
    //         for (const [key, value] of Object.entries(valueArray.value)) {
    //             console.log(key, value);
    //             // const size = value.edCoordX-value.stCoordX;
    //             console.log(value.stCoordX);
    //             // txt = "<div style='margin: " + value.stCoordX + "px " +value.stCoordY+"px'><input type='text' size=" + value.text.length + " value='" + value.text + "' /><br/></div>";
    //             txt = "<input type='text' size=" + value.text.length + " value='" + value.text + "' /><br/>";
    //             console.log(txt);
    //             document.getElementById("txtPanel").innerHTML += txt;
    //         }
    //     }else if(document.getElementById("txtPanel")){
    //         document.getElementById("txtPanel").innerHTML = "";
    //     }
    // };
    showImage = (idParam, path, unit,text) =>{
        document.getElementById("imageFile").src = path;
        document.getElementById("txtPanel").innerHTML = "";
        const txt = "<div style='position:absolute'>"+unit+" : <input type='text' value='" + text + "' size='100' /><br/></div>";
        document.getElementById("txtPanel").innerHTML += txt;
    };
    render() {
        // this.createTextBox();
        let dataValue = [];
        let imgDirectory = ""
        if (this.state.dataList.value !== undefined){
            dataValue = this.state.dataList.value;
        }
        // if (this.state.imgDir !== undefined){
        //     imgDirectory = this.state.imgDir;
        // }
        const divStyle = { 
            backgroundImage: 'url(' + imgDirectory + ')' 
            ,backgroundPosition: 'cover'
            ,backgroundRepeat: 'noRepeat'
            ,backgroundSize: 'cover'
            ,width: '850px'
            // ,height: '1200px'
            , height: 'auto'
        };
        return (
            <div>
                <h1>Invoice Data Process</h1><p/>
                Upload file(zip, pdf, png) : <input type="file" id="uploadFIle" name="uploadFIle[]" accept="image/png, image/jpeg, .zip, .pdf"  multiple></input>
                {/* <input type="text" id="uploadText"></input> */}
                <button type="button" onClick={this.fileUpload}>File Upload</button>
                <p/>
                Date : <input type="text" id="getDate"></input>
                <button type="button" onClick={this.callGetListData}>go Query</button>
                <p/>
                <table border="1">
                    <tbody>
                        <tr align="center">
                            <td width="50">id</td>
                            <td width="100">invoiceName</td>
                            <td width="100">unit</td>
                            <td width="1000">text</td>
                            <td width="100">filePath</td> 
                            <td width="100">updateDate</td>
                            <td width="100">updateTime</td>
                        </tr>
                        {
                            dataValue.map(row =>
                                (<BoardItem key={row.id} row={row} clickEvent={function(id, path, unit, text){
                                    // this.setState({id:id, imgDir:path});
                                    this.showImage(id, path, unit, text);
                                }.bind(this)}
                                />)
                            )   
                        }
                    </tbody>
                </table>
                <table border="1" width="1600" height="720">
                    <tbody>
                        <tr>
                            <td align="center">
                                <img src={imgDirectory} style={divStyle} id="imageFile"></img>
                            </td>
                            <td align="center">
                                {/* <img src={imgDirectory} class='imgClass'></img> */}
                                <div id="txtPanel" align="left" style={divStyle} ></div>
                            </td> 
                        </tr>
                    </tbody>
                </table>
                <button type="button" onClick={this.callUpdateData}>Update Data</button>
            </div>
        );
    }
}

export default InvoiceProcess;
